    void displayMenu(const string options[], int size, int selected, string extra)
    {
        system("cls"); // Clear the console screen
        cout << extra << '\n';
        for (int i = 0; i < size; i++)
        {
            if (i == selected) {
                cout << " > " << options[i] << " < " << endl; // Highlight selected option
            }
            else {
                cout << "   " << options[i] << endl;
            }
        }
    }

